sys_variable_value.value:
(function execute(inputs, outputs) {
    var stream = sn_fd.FlowAPI.executeDataStreamAction('sn_gcp_cf_spoke.fetch_operation', {
        "filter": inputs.filter,
    });
    var obj = []
    while (stream.hasNext()) {
        obj.push(stream.next())
    }
    var operations = [];
    obj.forEach(function(operationObj) {
        operations.push({
            "name": operationObj.name,
            "label": operationObj.name
        });
    })
    outputs.operations = {
        "data": operations
    }
})(inputs, outputs);

