sys_variable_value.value:
(function execute(inputs, outputs) {
    var stream = sn_fd.FlowAPI.executeDataStreamAction('sn_gcp_cf_spoke.fetch_function', {
        "project_id": inputs.projectid,
        "location": inputs.location
    });
    var obj = []
    while (stream.hasNext()) {
        obj.push(stream.next())
    }
    var functions = [];
    obj.forEach(function(functionObj) {
        functions.push({
            "name": functionObj.name,
            "label": functionObj.name
        });
    })
    outputs.functions = {
        "data": functions
    }
})(inputs, outputs);

